import React, { Component } from 'react';
import Topbar from '../navbar/Topbar';
import Navbar from '../navbar/Navbar';
import { auth } from '../firebase/firebase.utils';
import '../Trip/trip.css'
import { updateQuote } from '../firebase/firebase.utils'

class ProfileView extends Component {
  state = {
    userID: this.props.currentUser.id,
    quote: this.props.currentUser.quote
  };

  // componentDidMount(){
  //   this.props.currentUser.quote ? this.setState({
  //     quote: true
  //   }) : this.setState({
  //     quote: false
  //   })
  // }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();

    updateQuote(this.state.userID, this.state.quote);

  };

  onClick = (e) => {
    e.preventDefault();

    updateQuote(this.state.userID, '')
  }

  render() {
    const logOut = () => {
      auth.signOut();
      this.props.history.push("/");
    };
console.log(this.state.quote)
    return (
      <div>
        <br />
        <div className="App">
          <img
            src={this.props.currentUser.photoURL}
            alt={this.props.currentUser.displayName}
            style={{ width: "50vw", borderRadius: "50%" }}
          />
          <h1 style={{ fontFamily: "comic sans ms, cursive, sans-serif" }}>
            {this.props.currentUser.displayName}
          </h1>
          <hr />
          <h3 style={{ marginLeft: "5px", marginRight: "5px" }}>
            Email: {this.props.currentUser.email}
          </h3>
          {this.state.quote ? (

            <h1>{this.props.currentUser.quote}</h1>


          ) : (
            <form onSubmit={(e) => this.onSubmit(e)}>
              <input
                type="text"
                name="quote"
                placeholder="Quote"
                onChange={(e) => this.handleChange(e)}
              />
              <input type="submit" value="Post" />
            </form>
          )}
          <br />
          <form>
            <legend> Status </legend>
            <div>
              <label>
                {" "}
                <input type="radio" name="active" /> Active{" "}
              </label>
            </div>
            <div>
              <label>
                {" "}
                <input type="radio" name="active" /> Offline{" "}
              </label>
            </div>
          </form>

          <p>
            <button
              onClick={logOut}
              style={{
                backgroundColor: "red",
                color: "white",
                borderRadius: "50%",
                width: "80px",
                height: "30px",
              }}
            >
              Log Out
            </button>
          </p>
        </div>
      </div>
    );
  }
}

export default ProfileView;
