import React, { Component } from 'react';
import Talk from 'talkjs';
import {
    userList,
  } from '../firebase/firebase.utils';




import './chat.css'

export default class Chat extends Component {
  constructor(props) {
    super(props);
    this.talkjsContainer = React.createRef();
    this.state = {
        groupUsers: ''
    }
  }

  componentDidMount() {
    const currentUser = this.props.currentUser;
    userList()
    console.log(typeof currentUser.id);
    Talk.ready.then(() => {
      var me = new Talk.User({
        id: currentUser.id,
        name: currentUser.displayName,
        email: currentUser.email,
        photoUrl: currentUser.photoURL,
        welcomeMessage: 'Hey there! How are you? :-)',
      });

      window.talkSession = new Talk.Session({
        appId: 'taa7PJf6',
        me: me,
      });

      var other = new Talk.User({
        id: '654321',
        name: 'Sebastian',
        email: 'Sebastian@example.com',
        photoUrl: 'https://demo.talkjs.com/img/sebastian.jpg',
        welcomeMessage: 'Hey, how can I help?',
      });

      var conversation = window.talkSession.getOrCreateConversation(
        Talk.oneOnOneId(me, other)
      );
      conversation.setParticipant(me);
      conversation.setParticipant(other);

      var inbox = window.talkSession.createInbox({ selected: conversation });
      inbox.mount(this.talkjsContainer.current);
    });
  }

  render()



  {
    return <div className='chatbox-container' ref={this.talkjsContainer}></div>;
  }
}
