import React, { Component } from 'react';
import { fetchTripData } from '../firebase/firebase.utils';
import Progress from '../progress/Progress';

export default class PastTrip extends Component {
  state = {
    tripList: '',
    tripArr: '',
    userID: '',
    isData: false,
  };

  updateArr = async (tripList) => {

    fetchTripData(tripList)
  };

  componentDidMount() {

  let arr = this.props.currentUser.trips

  arr.map(el => {
      console.log(el)
  })

  }


  render() {

    const mapOverList = async (list) => {

        let arr = []

        list.map(async el=>{
            let x = await fetchTripData(el)
            arr.push(x)
        })
        console.log(arr)

    }

    const getInfo = async (arr) => {
        console.log(arr)
    }


    if(this.state.tripList) {
        mapOverList(this.state.tripList)
    }

    console.log(this.state.tripArr.length)

    if(this.state.tripArr.length) {
        getInfo(this.state.tripArr)
    }

    return this.props.tripList? <div>hellos!</div> : <Progress />;
  }
}
