import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { useHistory } from 'react-router-dom';
import ListSubheader from '@material-ui/core/ListSubheader';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Collapse from '@material-ui/core/Collapse';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import './PastTrips.css'
import { singleQuery } from '../firebase/firebase.utils';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}));

const updateUser = async (uID, tripID) => {
  await singleQuery(uID, tripID);
};

function goToTrip(props, uID, tripID, option) {
  updateUser(uID, tripID);

  switch (option) {
    case 1:
      props.history.push('/home/image');
      break;
    case 2:
      props.history.push('/home/receipt');
      break;

    default:
      break;
  }
}

export default function SimpleList({ arr, userID, props }) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const handleClick = () => {
    setOpen(!open);
  };
  console.log(props.currentUser)
  let image =
  'https://miro.medium.com/max/1200/1*mk1-6aYaf_Bes1E3Imhc0A.jpeg';


    return (
      <>
        <div style={{ overflow: 'scroll', height: '70vh' }}>
          <div className="base" style={{ textAlign: 'center' }}>
          arr.map((el, i) => {
            <Paper
            className="paper"
            style={{ backgroundColor: 'aliceblue', maxWidth: 300 }}
          >
            <Grid container spacing={2}>
              <Grid item>
                <img
                  style={{ width: '100vw' }}
                  className="img"
                  alt="complex"
                  src={image}
                />
              </Grid>
              <Grid item xs={12} sm container>
                <Grid item xs container direction="column" spacing={2}>
                  <Grid item xs>
                    <Typography gutterBottom variant="subtitle1">
                      el.name
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      el.date
                    </Typography>
                    <Typography variant="subtitle1">
                      <div
                        style={{ display: 'flex', justifyContent: 'center' }}
                      >
                        <div style={{ marginRight: '10px' }}>
                          {`What you owe: ${props.currentUser.expenses}`}
                        </div>
                        <button className="moneyButton">PAY $</button>
                      </div>
                    </Typography>
                    <Typography>Who to pay:</Typography>
                    {props.currentUser ? (
                      <Typography>(People you owe)</Typography>
                    ) : (
                      <Typography>You're all set!</Typography>
                    )}
                    <button className="button">Receipts</button> |{' '}
                    <button className="button">Images</button>
                  </Grid>
                </Grid>
              </Grid>
            </Grid>
          </Paper>
          });
          </div>
        </div>
        {/* <ListItem button onClick={handleClick}>
              <ListItemText primary={`${el.name}      -${el.date}`} />
              {open ? <ExpandLess /> : <ExpandMore />}
            </ListItem>
            <Collapse in={open} timeout="auto" unmountOnExit>
              <List component="div" disablePadding>
                <ListItem button className={classes.nested} onClick={()=> goToTrip(props, userID, el.id, 1 )}>
                  <ListItemText primary="Pictures" />
                </ListItem>
                <ListItem button className={classes.nested} onClick={()=> goToTrip(props, userID, el.id, 2 )}>
                  <ListItemText primary="Receipts" />
                </ListItem>
              </List>
            </Collapse> */}
      </>
    );

}
