import React, { Component } from 'react';
import {saveReceipt} from '../firebase/firebase.utils'
import axios from 'axios';
import ReceiptTable from './ReceiptTable';

let MY_URL_KEY = '31bb9650994211eab7efc1191d38e165';

export default class Receipt extends Component {

  state = {
    imageUrl: ''
  }


  getReceiptData = (imageUrl) => {
    console.log(imageUrl)
    let file = {
      url: imageUrl,
      headers: {
        'x-custom-key': 'string',
      },
    };

    axios
      .post('https://api.taggun.io/api/receipt/v1/simple/url', file, {
        headers: {
          apikey: MY_URL_KEY,
        },
      })
      .then(function (response) {
        saveReceipt(response.data.totalAmount.data)

      })
      .catch(function (error) {
        console.log(error);
      });

      this.setState({
        imageUrl: ''
      })
  };

  uploadAndScan = async (url) => {
    console.log(url)
    await this.getReceiptData(url)



  };

  handleFileUpload = (e) => {
    console.log('The file to be uploaded is: ', e.target.files[0]);
    const cloudName = 'dsxn0sfql';
    const preset = 'w8mpx9l6';
    let imgFile = e.target.files[0];

    var url = `https://api.cloudinary.com/v1_1/${cloudName}/upload`;
    var xhr = new XMLHttpRequest();
    var fd = new FormData();
    xhr.open('POST', url, true);
    xhr.setRequestHeader('X-Requested-With', 'XMLHttpRequest');

    xhr.onreadystatechange = (e) => {
      if (xhr.readyState == 4 && xhr.status == 200) {
        // File uploaded successfully
        var response = JSON.parse(xhr.responseText);
        console.log(response)
        // https://res.cloudinary.com/cloudName/image/upload/v1483481128/public_id.jpg
        var url = response.secure_url;
        // Create a thumbnail of the uploaded image, with 150px width
        this.setState({
          imageUrl: url
        })


      }
    };

    fd.append('upload_preset', preset);
    fd.append('tags', 'browser_upload'); // Optional - add tag for image admin in Cloudinary
    fd.append('file', imgFile);
    xhr.send(fd);


  };

  render() {
    return (
      <div>
          <ReceiptTable  />
        <button onClick={() => this.getReceiptData(this.state.imageUrl)}>Upload</button>
        <input type="file" onChange={(e) => this.handleFileUpload(e)} />
      </div>
    );
  }
}
