import React, { Component } from 'react'
import GroupImageFolder from './GroupImageFolder'
import { updateReceiptArr } from '../firebase/firebase.utils';
import axios from 'axios';

import Button from '@material-ui/core/Button';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import SaveIcon from '@material-ui/icons/Save';
import Grid from '@material-ui/core/Grid';
import ToastMessage from '../Toast/toastMessage';
import actions from '../../RouteContainer/axiosCalls'

export default class GroupImage extends Component {

state = {
          imageUrl: ""
        };


    // this method handles just the file upload
    handleFileUpload = async e => {
        console.log("The file to be uploaded is: ", e.target.files[0]);

        const uploadData = new FormData();
        // imageUrl => this name has to be the same as in the model since we pass
        // req.body to .create() method when creating a new thing in '/api/things/create' POST route
        uploadData.append("imageUrl", e.target.files[0]);

        await actions.uploadToCD(uploadData)
        .then(response => {
            // console.log('response is: ', response);
            // after the console.log we can see that response carries 'secure_url' which we can use to update the state 
            this.setState({ imageUrl: response.data.secure_url });
          })
          .catch(err => {
            console.log("Error while uploading the file: ", err);
          });

    }

    // this method submits the form
    handleSubmit = e => {
        e.preventDefault();

        axios.post('http://localhost:5000/things/create', this.state)
        .then(res => {
            console.log('added: ', res);
            // here you would redirect to some other page 
        })
        .catch(err => {
            console.log("Error while adding the thing: ", err);
        });
    }  


    render() {
        // const displayFolder = async () => {
        //     let res = await actions.imageList()
        //     console.log(res)
        //   }

        //   const createReqBody = async () => {
        //     let res = await actions.addImg({
        //         name: 'Alex',
        //         description: 'asdasd',
        //         imageUrl: 'asdasd'
        //     }) 
        //     console.log(res)
        //   }

        return (
            <div>
        <div className="table">
          <GroupImageFolder />
        </div>

        <Grid container justify="center" alignItems="center">
          <Button
            variant="contained"
            color="primary"
            size="small"
            // className={classes.button}
            startIcon={<SaveIcon />}
            // onClick={() => this.uploadAndScan(this.state.imageUrl)}
          >
            Save
          </Button>
          <input
            accept="image/*"
            className="uploadFile groupBtn"
            id="contained-button-file"
            multiple
            type="file"
            onChange={(e) => this.handleFileUpload(e)}
          />
          <label htmlFor="contained-button-file">
            <Button
              variant="contained"
              color="default"
              size="small"
              component="span"
              className="groupBtn"
              startIcon={<CloudUploadIcon />}
            >
              Upload
            </Button>
          </label>
        </Grid>
       {/* {
         display ? <ToastMessage /> : ''
       } */}

      </div>
        )
    }
}
