import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom'
import './PastTrips.css'


class ContactUs extends Component {
    state = {
        creators: [
            {
                name: 'Billy Zedan',
                img: '',
                description: 'Front-End Developer - Javascript, React, HTML, CSS, C and C++',
                email: 'billyzedan123@gmail.com',
                linkedIn: 'https://www.linkedin.com/in/billy-zedan/'
            },
            {
                name: 'Hugo Suarez',
                img: '',
                description: 'Software Developer - JavaScript, React.js, Express.js, Node.js​, ​MongoDB',
                email: 'hugo.suarez92@yahoo.com',
                linkedIn: 'https://www.linkedin.com/in/hugosuarezjr/'
            },
            {
                name: 'Alexander Victoria',
                img: '',
                description: 'Software Developer - JavaScript, React.js, Express.js, Node.js​, ​MongoDB',
                email: 'alexxdav97@gmail.com',
                linkedIn: 'https://www.linkedin.com/in/dazpel/'
            }
        ]
    }
    showTrips = () => {
        return this.state.trips.map((trip)=> {
          return (
            <div style={{overflow: 'scroll', height: '85vh'}}>
            <div className='base' style={{textAlign: 'center'}} >
            <br/>
              <Paper className='paper' style={{backgroundColor: 'aliceblue', maxWidth: 300}}>
                <Grid container spacing={2}>
                  <Grid item>
                      <Link to='/TripImage'><img style={{width: '100vw'}} className='img' alt="complex" src={trip.image} /></Link>
                  </Grid>
                  <Grid item xs={12} sm container>
                    <Grid item xs container direction="column" spacing={2}>
                      <Grid item xs>
                        <Typography gutterBottom variant="subtitle1">
                          {trip.tripName}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {trip.date}
                        </Typography>
                        <Typography variant="subtitle1">
                        <div style={{display: 'flex', justifyContent: 'center'}}>
                        <div style={{marginRight: '10px'}}>What you owe: {trip.tripExpense}</div>
                        <button className="moneyButton" >PAY $</button>
                        </div>
                        </Typography>
                        <Typography>
                          Who to pay:
                        </Typography>
                        {this.props.currentUser ? <Typography>
                          (People you owe)
                        </Typography>: <Typography>You're all set!</Typography>}
                        <button className='button'>Receipts</button> | <button className='button'>Images</button>
                      </Grid>
                    </Grid>
                  </Grid>
                </Grid>
              </Paper>
            </div>
            </div>
          )
        })
      }
    render() {
        return (
            <>
            <div style={{textAlign: 'center'}}>
                <h1>The Creators</h1>
            </div>
            <img src=".../public/HugoPic.jpg" alt='me'/>
             {/* {this.showContacts()} */}
            </>
        );
    }
}

export default ContactUs;
