  renderTripTable = async (tripList) => {
    let tripsData = []
    if (tripList.length > 0){
       await tripList.map(async el=> {
            try {
                let item = await fetchTripData(el)
                tripsData.push(item)
              } catch (err) {
                console.log(err);
              }
        })
    }
    console.log(tripsData, tripsData.date)
    return (<div>Hellos!</div>)
    }

  render() {



    return this.props.currentUser ? (this.renderTripTable(this.props.currentUser.trips)) : <Progress />
  }
}
