import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { useHistory } from 'react-router-dom';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { singleQuery } from '../firebase/firebase.utils';



const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}));

function ListItemLink(props) {
  return <ListItem button component="a" {...props} />;
}

const updateUser = async (uID, tripID) => {
    await singleQuery(uID, tripID);
  }


function goToTrip  (uID, tripID, option) {

    console.log(this.props)

     updateUser(uID, tripID);

//   switch (option) {
//     case 1:
//       history.push('/home/images');
//       break;
//     case 2:
//       history.push('/home/receipts');
//       break;

//     default:
//       break;
//   }
};

export default function SimpleList({ arr, userID }) {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <List component="nav" aria-label="secondary mailbox folders">
        {arr.map((el, i) => {
          return (
            <ExpansionPanel>
              <ExpansionPanelSummary
                expandIcon={<ExpandMoreIcon />}
                aria-controls="panel1a-content"
                id="panel1a-header"
                key={i}
              >
                <Typography className={classes.heading}>
                  {`${el.name}          -${el.date}`}
                </Typography>
              </ExpansionPanelSummary>
              <ExpansionPanelDetails>
                <ListItem
                  button
                  key={i}
                  onClick={() => goToTrip(userID, el.id, 1)}
                >
                  <ListItemText primary="Pictures" />
                </ListItem>
                <Divider />
                <ListItem
                  button
                  key={i}
                  onClick={() => goToTrip(userID, el.id, 2)}
                >
                  <ListItemText primary="Receipts" />
                </ListItem>
                <Divider />
              </ExpansionPanelDetails>
            </ExpansionPanel>
          );
        })}
      </List>
    </div>
  );
}
