import React, { Component } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Progress from '../progress/Progress';
import { receiptListArr } from '../firebase/firebase.utils';
import { Link } from 'react-router-dom';

export default class ReceiptTable extends Component {
  state = {
    images: '',
  };

  async componentDidMount() {
    let receiptImagesArr = await receiptListArr();

    this.setState({
        images: receiptImagesArr,
    });
  }

  render() {
    const receipTable = (list) => {
      console.log(list);

      return (
        <React.Fragment>

              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell className="bold">Image</TableCell>
                    <TableCell className="bold" align="right">
                      Upload Date
                    </TableCell>
                    {/* <TableCell className="bold" align="right">
                      Amount
                    </TableCell> */}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {list.map((el, i) => (
                    <TableRow key={el.id}>
                      <TableCell className="bold">
                  <a href={el.imgUrl}>{`Receipt ${i}`}</a>
                      </TableCell>
                      <TableCell align="right">{el.createdAt}</TableCell>
                      {/* <TableCell align="right">{symbolData[i].data.h}</TableCell> */}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

        </React.Fragment>
      );
    };

    const { images } = this.state;

    return <div>{images.length > 0 ? receipTable(images) : <Progress />}</div>;
  }
}
