import React, { Component } from 'react';
import { fetchTripData } from '../firebase/firebase.utils';
import Progress from '../progress/Progress';

export default class PastTrip extends Component {
  state = {
    tripList: [],
    userID: '',
    isData: false,
  };

  updateArr = async (tripList) => {

    this.setState({
        tripList: tripList,
        isData: true,
    });
  };

  componentDidMount() {
  this.updateArr(this.props.currentUser.trips)

  }


  render() {

    const mapOverList = async (list) => {

        let arr = []

        list.map(async el=>{
            let x = await fetchTripData(el)
            arr.push(x)
        })

        console.log(arr)
    }

    if(this.state.tripList.length>0) {
        mapOverList(this.state.tripList)
    }

    return this.props.currentUser ? <div>hellos!</div> : <Progress />;
  }
}
