import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom'
import './PastTrips.css'


class ContactUs extends Component {
    state = {
        creators: [
            {
                name: 'Billy Zedan',
                img: '/Billy.jpeg',
                description: 'Front-End Developer - Javascript, React, HTML, CSS, C and C++',
                email: 'billyzedan123@gmail.com',
                linkedIn: 'https://www.linkedin.com/in/billy-zedan/'
            },
            {
                name: 'Hugo Suarez',
                img: '/HugoProfilePic.jpeg',
                description: 'Software Developer - JavaScript, React.js, Express.js, Node.js​, ​MongoDB',
                email: 'hugo.suarez92@yahoo.com',
                linkedIn: 'https://www.linkedin.com/in/hugosuarezjr/'
            },
            {
                name: 'Alexander Victoria',
                img: '/Alex.jpeg',
                description: 'Software Developer - JavaScript, React.js, Express.js, Node.js​, ​MongoDB',
                email: 'alexxdav97@gmail.com',
                linkedIn: 'https://www.linkedin.com/in/dazpel/'
            }
        ]
    }
    showTrips = () => {
        return this.state.creators.map((creator) => {
          return (
            <div style={{overflow: 'scroll', height: '85vh'}}>
            <div className='base' style={{textAlign: 'center'}} >
            <br/>
              <Paper className='paper' style={{backgroundColor: 'aliceblue', maxWidth: 300}}>
                <Grid container spacing={2}>
                  <Grid item>
                      <Link to='/TripImage'><img style={{width: '100vw'}} className='img' alt="complex" src={creator.img} /></Link>
                  </Grid>
                  <Grid item xs={12} sm container>
                    <Grid item xs container direction="column" spacing={2}>
                      <Grid item xs>
                        <Typography gutterBottom variant="subtitle1">
                          {creator.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {creator.description}
                        </Typography>
                        <Typography variant="subtitle1">
                        <div style={{display: 'flex', justifyContent: 'center'}}>
                        <div style={{marginRight: '10px'}}>Contact:</div>
                        <ul>
                            <li>
                                <a href={creator.email}>{creator.email}</a>
                            </li>
                            <li>
                                <a href={creator.linkedIn}>{creator.linkedIn}</a>
                            </li>
                        </ul>
                        </div>
                        </Typography>
                      </Grid>
                    </Grid>
                  </Grid>
                </Grid>
              </Paper>
            </div>
            </div>
          )
        })
      }
    render() {
        return (
            <>
            <div style={{textAlign: 'center'}}>
                <h1>The Creators</h1>
            </div>
            <img src="/HugoProfilePic.jpeg" alt='me'/>
             {/* {this.showContacts()} */}
            </>
        );
    }
}

export default ContactUs;
